def concat(e, a=None):
    res = ""
    for i in e:
        res = res + str(i)
    if a != None:
        for j in a:
            res = res + str(j)
    return res

def bitstring_to_bytes(s, little=True):
    if little:
        return int(s, 2).to_bytes((len(s) + 7) // 8, byteorder='little')
    else:
        return int(s, 2).to_bytes((len(s) + 7) // 8, byteorder='big')

